import React, { useState, useEffect, useMemo, useRef } from "react";
import freelancerService from "../services/FreelancerService";
import { useTable } from "react-table";

const HomePage = (props) => {
  const [freelancers, setFreelancers] = useState([]);
  const [searchPhrase, setSearchPhrase] = useState("");
  const [currentPageNumber, setCurrentPageNumber] = useState(1);
  const [pageSize] = useState(10);
  const freelancersRef = useRef();

  freelancersRef.current = freelancers;

  useEffect(() => {
    retrieveFreelancers();
  }, []);

  const onChangeSearchPhrase = (e) => {
    const searchPhrase = e.target.value;
    setSearchPhrase(searchPhrase);
  };

  const retrieveFreelancers = () => {
    freelancerService.getAll(currentPageNumber, pageSize, null, searchPhrase)
      .then((response) => {
        setFreelancers(response.data);
      })
      .catch((e) => {
        console.log(e);
      });
  };

  const refreshList = () => {
    setSearchPhrase("");
    setCurrentPageNumber(1);
    retrieveFreelancers();
  };

  const findBySearchPhrase = () => {
    setCurrentPageNumber(1);
    retrieveFreelancers();
  };

  // navigate to details page of a freelancer
  const openFreelancer = (rowIndex) => {
    const id = freelancersRef.current[rowIndex].id;
    props.history.push("/freelancers/" + id);
  };

  const deleteFreelancer = (rowIndex) => {
    const id = freelancersRef.current[rowIndex].id;
    freelancerService.remove(id)
      .then((response) => {
        props.history.push("/freelancers");
        let newFreelancers = [...freelancersRef.current];
        newFreelancers.splice(rowIndex, 1);
        setFreelancers(newFreelancers);
      })
      .catch((e) => {
        console.log(e);
      });
  };

  const archiveFreelancer = (rowIndex) => {
    const id = freelancersRef.current[rowIndex].id;
    freelancerService.archive(id)
      .then((response) => {
        console.log(response.data);
        refreshList();
      })
      .catch((e) => {
        console.log(e);
      });
  };

  const unarchiveFreelancer = (rowIndex) => {
    const id = freelancersRef.current[rowIndex].id;
    freelancerService.unarchive(id)
      .then((response) => {
        console.log(response.data);
        refreshList();
      })
      .catch((e) => {
        console.log(e);
      });
  };

  const columns = useMemo(
    () => [
      {
        Header: "Username",
        accessor: "username",
      },
      {
        Header: "Email",
        accessor: "email",
      },
      {
        Header: "Phone Number",
        accessor: "phoneNum",
      },
      {
        Header: "Skillsets",
        accessor: "skillsets",
      },
      {
        Header: "Hobbies",
        accessor: "hobbies",
      },
      {
        Header: "Actions",
        accessor: "actions",
        Cell: (props) => {
          const rowIdx = props.row.id;
          const isArchived = freelancersRef.current[rowIdx]?.isArchived;
          return (
            <div>
              <span onClick={() => openFreelancer(rowIdx)} title="Edit">
                <i className="far fa-edit action mr-2" style={{cursor: 'pointer'}}></i>
              </span>
              {isArchived ? (
                <span onClick={() => unarchiveFreelancer(rowIdx)} title="Unarchive">
                  <i className="fas fa-undo action mr-2" style={{cursor: 'pointer', color: 'green'}}></i>
                </span>
              ) : (
                <span onClick={() => archiveFreelancer(rowIdx)} title="Archive">
                  <i className="fas fa-archive action mr-2" style={{cursor: 'pointer', color: 'orange'}}></i>
                </span>
              )}
              <span onClick={() => deleteFreelancer(rowIdx)} title="Delete">
                <i className="fas fa-trash action" style={{cursor: 'pointer', color: 'red'}}></i>
              </span>
            </div>
          );
        },
      },
    ],
    []
  );

  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    rows,
    prepareRow,
  } = useTable({
    columns,
    data: freelancers,
  });

  return (
    <div className="list row">
      <div className="col-md-8">
        <div className="input-group mb-3">
          <input
            type="text"
            className="form-control"
            placeholder="Search by username or email"
            value={searchPhrase}
            onChange={onChangeSearchPhrase}
          />
          <div className="input-group-append">
            <button
              className="btn btn-outline-secondary"
              type="button"
              onClick={findBySearchPhrase}
            >
              Search
            </button>
          </div>
        </div>
      </div>
      <div className="col-md-12 list">
        <table
          className="table table-striped table-bordered"
          {...getTableProps()}
        >
          <thead>
            {headerGroups.map((headerGroup) => (
              <tr {...headerGroup.getHeaderGroupProps()}>
                {headerGroup.headers.map((column) => (
                  <th {...column.getHeaderProps()}>
                    {column.render("Header")}
                  </th>
                ))}
              </tr>
            ))}
          </thead>
          <tbody {...getTableBodyProps()}>
            {rows.map((row, i) => {
              prepareRow(row);
              return (
                <tr {...row.getRowProps()}>
                  {row.cells.map((cell) => {
                    return (
                      <td {...cell.getCellProps()}>{cell.render("Cell")}</td>
                    );
                  })}
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default HomePage;
