import { useNavigate } from 'react-router-dom';

import React, { useState, useEffect } from 'react';

function HomePage() {
  const navigate = useNavigate();
  const [freelancers, setFreelancers] = useState([]);

  useEffect(() => {
    async function fetchFreelancers() {
      try {
        const response = await fetch('https://localhost:7202/api/Freelancers/filter');
        if (response.ok) {
          const data = await response.json();
          setFreelancers(data);
        }
      } catch (error) {
        console.error('Error fetching freelancers:', error);
      }
    }
    fetchFreelancers();
  }, []);

  const unarchiveFreelancer = (idx) => {
    setFreelancers(prev =>
      prev.map((f, i) =>
        i === idx ? { ...f, isArchived: false } : f
      )
    );
  };

  const archiveFreelancer = (idx) => {
    setFreelancers(prev =>
      prev.map((f, i) =>
        i === idx ? { ...f, isArchived: true } : f
      )
    );
  };

  const deleteFreelancer = (idx) => {
    setFreelancers(prev => prev.filter((_, i) => i !== idx));
  };

  return (
    <div className="HomePage">
      <h1>Welcome to Freelancer UI</h1>
      <button onClick={() => navigate('/create')} className="btn btn-primary">Create New Freelancer</button>
        <div className="col-md-12 list">
          <table className="table table-striped table-bordered">
            <thead>
              <tr>
                <th>Username</th>
                <th>Email</th>
                <th>Phone Number</th>
                <th>Skillsets</th>
                <th>Hobbies</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {freelancers.map((f, idx) => (
                <tr key={f.id || idx}>
                  <td>{f.username || f.Username}</td>
                  <td>{f.email || f.Email}</td>
                  <td>{f.phoneNum || f.PhoneNum}</td>
                  <td>{Array.isArray(f.Skillsets) && f.Skillsets.length > 0 ? f.Skillsets.map(s => s.SkillName).join(', ') : '—'}</td>
                  <td>{Array.isArray(f.Hobbies) && f.Hobbies.length > 0 ? f.Hobbies.map(h => h.HobbyName).join(', ') : '—'}</td>
                  <td>
                    {/* Actions: View/Archive/Unarchive/Delete */}
                    <button className="btn btn-info btn-sm" onClick={() => navigate(`/freelancers/${f.id || idx}`)}>View</button>
                    {f.isArchived ? (
                      <button className="btn btn-success btn-sm" onClick={() => unarchiveFreelancer(idx)}>Unarchive</button>
                    ) : (
                      <button className="btn btn-warning btn-sm" onClick={() => archiveFreelancer(idx)}>Archive</button>
                    )}
{/*                     <button className="btn btn-danger btn-sm ml-2" onClick={() => deleteFreelancer(idx)}>Delete</button>
 */}                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
    </div>
  );
}

export default HomePage;
