import React, { useState } from 'react';

function CreateFreelancer() {
  const initialFreelancerState = {
    id: null,
    username: '',
    email: '',
    phoneNum: '',
  skillsets: '',
  hobbies: ''
  };
  const [freelancer, setFreelancer] = useState(initialFreelancerState);
  const [submitted, setSubmitted] = useState(false);

  const handleInputChange = event => {
    const { name, value } = event.target;
    setFreelancer({ ...freelancer, [name]: value });
  };


  const saveFreelancer = async () => {
    const data = {
      username: freelancer.username,
      email: freelancer.email,
      phoneNum: freelancer.phoneNum,
      skillsets: freelancer.skillsets,
      hobbies: freelancer.hobbies
    };
    try {
  const response = await fetch('https://localhost:7202/api/Freelancers', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data)
      });
      if (response.ok) {
        const result = await response.json();
        setFreelancer({ ...freelancer, id: result.id });
        setSubmitted(true);
        console.log(result);
      } else {
        alert('Error submitting form');
      }
    } catch (error) {
      console.error('Error:', error);
      alert('Network error');
    }
  };

  const newFreelancer = () => {
    setFreelancer(initialFreelancerState);
    setSubmitted(false);
  };

  return (
    <div className="submit-form">
      {submitted ? (
        <div>
          <h4>You submitted successfully!</h4>
          <button className="btn btn-success" onClick={newFreelancer}>
            Add
          </button>
        </div>
      ) : (
        <div>
          <div className="form-group">
            <label htmlFor="username">Username</label>
            <input
              type="text"
              className="form-control"
              id="username"
              required
              value={freelancer.username}
              onChange={handleInputChange}
              name="username"
            />
          </div>
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              className="form-control"
              id="email"
              required
              value={freelancer.email}
              onChange={handleInputChange}
              name="email"
            />
          </div>
          <div className="form-group">
            <label htmlFor="phoneNum">Phone Number</label>
            <input
              type="tel"
              className="form-control"
              id="phoneNum"
              required
              value={freelancer.phoneNum}
              onChange={handleInputChange}
              name="phoneNum"
            />
          </div>
          <div className="form-group">
            <label htmlFor="skillsets">Skillsetsets (comma separated)</label>
            <input
              type="text"
              className="form-control"
              id="skillsets"
              value={freelancer.skillsets}
              onChange={handleInputChange}
              name="skillsets"
              placeholder="e.g. JavaScript,Python,React"
            />
          </div>
          <div className="form-group">
            <label htmlFor="hobbies">Hobbies (comma separated)</label>
            <input
              type="text"
              className="form-control"
              id="hobbies"
              value={freelancer.hobbies}
              onChange={handleInputChange}
              name="hobbies"
              placeholder="e.g. Reading,Coding,Music"
            />
          </div>
          <button onClick={saveFreelancer} className="btn btn-success">
            Submit
          </button>
        </div>
      )}
    </div>
  );
}

export default CreateFreelancer;
